				if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { pid[index].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1'].toString(); }

				// NTE.2, sourceOfComment
				if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { pid[index].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1'].toString(); }

				// NTE.3, comment
				pid[index].NTE[indexNTE].comment = [];
				for each (field in nteSeg['NTE.3']) {
					var block = {};
					if (field['NTE.3.1'].toString() != "") { 
						pid[index].NTE[indexNTE].comment.push(field['NTE.3.1'].toString());
					}
				}
				if (pid[index].NTE[indexNTE].comment.length == 0) {
					delete pid[index].NTE[indexNTE].comment;
				}

				// clean up NTE and increment index
				if (Object.keys(pid[index].NTE[indexNTE]).length == 0) {
					delete pid[index].NTE[indexNTE];
				} else {
					indexNTE++;
				}
			}
		}

		// clean up PID and increment index
		if (Object.keys(pid[index]).length == 0) {
			delete pid[index];
		} else {
			index++;
		}
	}
}

// Optional: save a copy of the completed PID segment for reference later
//channelMap.put("pid", pid); // uncomment to save

// If it has content, add the PID segment to the hl7JSON object
if (pid && Object.keys(pid).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.PID = pid;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

// Pull out the PID JSON to embed the PV1 segments in
var pid = JSON.parse($('hl7JSON')).content.PID;

// PID iteration, in order to grab related PV1's
if (msg['PID'].length() > 0) {

	// PID is a required to have a PV1, but if the PID was blank and was removed from the JSON, just create a new one to add the PV1 into
	if (!pid) {
		pid = [{}];
	}

	var index = 0;
	for each (seg in msg.PID) {

		// PV1 (PID)
		var pv1Group = getSegmentsAfter(msg,seg,'PV1',false);
		if (pv1Group.length > 0 && pid) {
			pid[index].PV1 = [];
			var indexPV1 = 0;
			for each (pv1Seg in pv1Group) {
				pid[index].PV1[indexPV1] = {};

				// PV1.1, setId
				if (pv1Seg['PV1.1']['PV1.1.1'].toString() != "") { pid[index].PV1[indexPV1].setId = pv1Seg['PV1.1']['PV1.1.1'].toString(); }

				// PV1.2, patientClass
				if (pv1Seg['PV1.2']['PV1.2.1'].toString() != "") { pid[index].PV1[indexPV1].patientClass = pv1Seg['PV1.2']['PV1.2.1'].toString(); }

				// PV1.3, assignedPatientLocation
				pid[index].PV1[indexPV1].assignedPatientLocation = {};
				if (pv1Seg['PV1.3']['PV1.3.1'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.pointOfCare = pv1Seg['PV1.3']['PV1.3.1'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.2'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.room = pv1Seg['PV1.3']['PV1.3.2'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.3'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.bed = pv1Seg['PV1.3']['PV1.3.3'].toString(); }
				pid[index].PV1[indexPV1].assignedPatientLocation.facility = {};
				if (pv1Seg['PV1.3']['PV1.3.4']['PV1.3.4.1'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.facility.nameSpaceId =  pv1Seg['PV1.3']['PV1.3.4']['PV1.3.4.1'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.4']['PV1.3.4.2'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.facility.universalId =  pv1Seg['PV1.3']['PV1.3.4']['PV1.3.4.2'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.4']['PV1.3.4.3'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.facility.universalIdType =  pv1Seg['PV1.3']['PV1.3.4']['PV1.3.4.3'].toString(); }
				if (Object.keys(pid[index].PV1[indexPV1].assignedPatientLocation.facility).length == 0) {
					if (pv1Seg['PV1.3']['PV1.3.4'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.facility.nameSpaceId = pv1Seg['PV1.3']['PV1.3.4'].toString(); }
					else delete pid[index].PV1[indexPV1].assignedPatientLocation.facility;
				}
				if (pv1Seg['PV1.3']['PV1.3.5'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.locationStatus = pv1Seg['PV1.3']['PV1.3.5'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.6'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.personLocationType = pv1Seg['PV1.3']['PV1.3.6'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.7'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.building = pv1Seg['PV1.3']['PV1.3.7'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.8'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.floor = pv1Seg['PV1.3']['PV1.3.8'].toString(); }
				if (pv1Seg['PV1.3']['PV1.3.9'].toString() != "") { pid[index].PV1[indexPV1].assignedPatientLocation.locationDescription = pv1Seg['PV1.3']['PV1.3.9'].toString(); }
				if (Object.keys(pid[index].PV1[indexPV1].assignedPatientLocation).length == 0) {
					delete pid[index].PV1[indexPV1].assignedPatientLocation;
				}

				// PV1.4, admissionType
				if (pv1Seg['PV1.4']['PV1.4.1'].toString() != "") { pid[index].PV1[indexPV1].admissionType = pv1Seg['PV1.4']['PV1.4.1'].toString(); }

				// PV1.5, preadmitNumber
				pid[index].PV1[indexPV1].preadmitNumber = {};
				if (pv1Seg['PV1.5']['PV1.5.1'].toString() != "") { pid[index].PV1[indexPV1].preadmitNumber.id = pv1Seg['PV1.5']['PV1.5.1'].toString(); }
				if (pv1Seg['PV1.5']['PV1.5.2'].toString() != "") { pid[index].PV1[indexPV1].preadmitNumber.identifierCheckDigit = pv1Seg['PV1.5']['PV1.5.2'].toString(); }
				if (pv1Seg['PV1.5']['PV1.5.3'].toString() != "") { pid[index].PV1[indexPV1].preadmitNumber.codeIdentifyingCheckDigitScheme = pv1Seg['PV1.5']['PV1.5.3'].toString(); }
