				if (pv1Seg['PV1.5']['PV1.5.4']['PV1.5.4.2'].toString() != "") { pid[index].PV1[indexPV1].preadmitNumber.assigningAuthority.universalId = pv1Seg['PV1.5']['PV1.5.4']['PV1.5.4.2'].toString(); }
				if (pv1Seg['PV1.5']['PV1.5.4']['PV1.5.4.3'].toString() != "") { pid[index].PV1[indexPV1].preadmitNumber.assigningAuthority.universalIdType = pv1Seg['PV1.5']['PV1.5.4']['PV1.5.4.3'].toString(); }
				if (Object.keys(pid[index].PV1[indexPV1].preadmitNumber.assigningAuthority).length == 0) {
					if (pv1Seg['PV1.5']['PV1.5.4'].toString() != "") { pid[index].PV1[indexPV1].preadmitNumber.assigningAuthority.namespaceId = pv1Seg['PV1.5']['PV1.5.4'].toString(); }
