					else delete pid[index].PV1[indexPV1].preadmitNumber.assigningFacility;
				}
				if (Object.keys(pid[index].PV1[indexPV1].preadmitNumber).length == 0) {
					delete pid[index].PV1[indexPV1].preadmitNumber;
				}

				// PV1.6, priorPatientLocation
				pid[index].PV1[indexPV1].priorPatientLocation = {};
				if (pv1Seg['PV1.6']['PV1.6.1'].toString() != "") { pid[index].PV1[indexPV1].priorPatientLocation.pointOfCare = pv1Seg['PV1.6']['PV1.6.1'].toString(); }
				if (pv1Seg['PV1.6']['PV1.6.2'].toString() != "") { pid[index].PV1[indexPV1].priorPatientLocation.room = pv1Seg['PV1.6']['PV1.6.2'].toString(); }
				if (pv1Seg['PV1.6']['PV1.6.3'].toString() != "") { pid[index].PV1[indexPV1].priorPatientLocation.bed = pv1Seg['PV1.6']['PV1.6.3'].toString(); }
