				if (pv1Seg['PV1.6']['PV1.6.4']['PV1.6.4.3'].toString() != "") { pid[index].PV1[indexPV1].priorPatientLocation.facility.universalIdType =  pv1Seg['PV1.6']['PV1.6.4']['PV1.6.4.3'].toString(); }
