					if (field['PV1.7.11'].toString() != "") { block.identifierCheckDigit = field['PV1.7.11'].toString(); }
					if (field['PV1.7.12'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['PV1.7.12'].toString(); }
