				// PV1.11, temporaryLocation
				pid[index].PV1[indexPV1].temporaryLocation = {};
				if (pv1Seg['PV1.11']['PV1.11.1'].toString() != "") { pid[index].PV1[indexPV1].temporaryLocation.pointOfCare = pv1Seg['PV1.11']['PV1.11.1'].toString(); }
				if (pv1Seg['PV1.11']['PV1.11.2'].toString() != "") { pid[index].PV1[indexPV1].temporaryLocation.room = pv1Seg['PV1.11']['PV1.11.2'].toString(); }
