				// PV1.45, dischargeDateTime
				if (pv1Seg['PV1.45']['PV1.45.1'].toString() != "") { pid[index].PV1[indexPV1].dischargeDateTime = pv1Seg['PV1.45']['PV1.45.1'].toString(); }

				// PV1.46, currentPatientBalance
				if (pv1Seg['PV1.46']['PV1.46.1'].toString() != "") { pid[index].PV1[indexPV1].currentPatientBalance = pv1Seg['PV1.46']['PV1.46.1'].toString(); }

				// PV1.47, totalCharges
				if (pv1Seg['PV1.47']['PV1.47.1'].toString() != "") { pid[index].PV1[indexPV1].totalCharges = pv1Seg['PV1.47']['PV1.47.1'].toString(); }

				// PV1.48, totalAdjustments
				if (pv1Seg['PV1.48']['PV1.48.1'].toString() != "") { pid[index].PV1[indexPV1].totalAdjustments = pv1Seg['PV1.48']['PV1.48.1'].toString(); }

				// PV1.49, totalPayments
				if (pv1Seg['PV1.49']['PV1.49.1'].toString() != "") { pid[index].PV1[indexPV1].totalPayments = pv1Seg['PV1.49']['PV1.49.1'].toString(); }

				// PV1.50, alternateVisitID
				pid[index].PV1[indexPV1].alternateVisitID = {};
				if (pv1Seg['PV1.50']['PV1.50.1'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.id = pv1Seg['PV1.50']['PV1.50.1'].toString(); }
				if (pv1Seg['PV1.50']['PV1.50.2'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.identifierCheckDigit = pv1Seg['PV1.50']['PV1.50.2'].toString(); }
				if (pv1Seg['PV1.50']['PV1.50.3'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.codeIdentifyingCheckDigitScheme = pv1Seg['PV1.50']['PV1.50.3'].toString(); }
				pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority = {};
				if (pv1Seg['PV1.50']['PV1.50.4']['PV1.50.4.1'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority.namespaceId = pv1Seg['PV1.50']['PV1.50.4']['PV1.50.4.1'].toString(); }
				if (pv1Seg['PV1.50']['PV1.50.4']['PV1.50.4.2'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority.universalId = pv1Seg['PV1.50']['PV1.50.4']['PV1.50.4.2'].toString(); }
				if (pv1Seg['PV1.50']['PV1.50.4']['PV1.50.4.3'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority.universalIdType = pv1Seg['PV1.50']['PV1.50.4']['PV1.50.4.3'].toString(); }
				if (Object.keys(pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority).length == 0) {
					if (pv1Seg['PV1.50']['PV1.50.4'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority.namespaceId = pv1Seg['PV1.50']['PV1.50.4'].toString(); }
					else delete pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority;
				}
				if (pv1Seg['PV1.50']['PV1.50.5'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.idTypeCode = pv1Seg['PV1.50']['PV1.50.5'].toString(); }
				pid[index].PV1[indexPV1].alternateVisitID.assigningFacility = {};
				if (pv1Seg['PV1.50']['PV1.50.6']['PV1.50.6.1'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningFacility.namespaceId = pv1Seg['PV1.50']['PV1.50.6']['PV1.50.6.1'].toString(); }
				if (pv1Seg['PV1.50']['PV1.50.6']['PV1.50.6.2'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningFacility.universalId = pv1Seg['PV1.50']['PV1.50.6']['PV1.50.6.2'].toString(); }
				if (pv1Seg['PV1.50']['PV1.50.6']['PV1.50.6.3'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningFacility.universalIdType = pv1Seg['PV1.50']['PV1.50.6']['PV1.50.6.3'].toString(); }
				if (Object.keys(pid[index].PV1[indexPV1].alternateVisitID.assigningFacility).length == 0) {
					if (pv1Seg['PV1.50']['PV1.50.6'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningFacility.namespaceId = pv1Seg['PV1.50']['PV1.50.6'].toString(); }
					else delete pid[index].PV1[indexPV1].alternateVisitID.assigningFacility;
				}
				if (Object.keys(pid[index].PV1[indexPV1].alternateVisitID).length == 0) {
					delete pid[index].PV1[indexPV1].alternateVisitID;
				}

				// PV1.51, visitIndicator
				if (pv1Seg['PV1.51']['PV1.51.1'].toString() != "") { pid[index].PV1[indexPV1].visitIndicator = pv1Seg['PV1.51']['PV1.51.1'].toString(); }

				// PV1.52, otherHealthcareProvider
				pid[index].PV1[indexPV1].otherHealthcareProvider = {};
				if (pv1Seg['PV1.52']['PV1.52.1'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.id = pv1Seg['PV1.52']['PV1.52.1'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.2'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.lastName = pv1Seg['PV1.52']['PV1.52.2'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.3'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.firstName = pv1Seg['PV1.52']['PV1.52.3'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.4'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.middleInitOrName = pv1Seg['PV1.52']['PV1.52.4'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.5'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.suffix = pv1Seg['PV1.52']['PV1.52.5'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.6'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.prefix = pv1Seg['PV1.52']['PV1.52.6'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.7'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.degree = pv1Seg['PV1.52']['PV1.52.7'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.8'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.sourceTable = pv1Seg['PV1.52']['PV1.52.8'].toString(); }
				pid[index].PV1[indexPV1].otherHealthcareProvider.assigningAuthority = {};
				if (pv1Seg['PV1.52']['PV1.52.9']['PV1.52.9.1'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningAuthority.id = pv1Seg['PV1.52']['PV1.52.9']['PV1.52.9.1'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.9']['PV1.52.9.2'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningAuthority.universalId = pv1Seg['PV1.52']['PV1.52.9']['PV1.52.9.2'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.9']['PV1.52.9.3'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningAuthority.universalIdType = pv1Seg['PV1.52']['PV1.52.9']['PV1.52.9.3'].toString(); }
				if (Object.keys(pid[index].PV1[indexPV1].otherHealthcareProvider.assigningAuthority).length == 0) {
					if (pv1Seg['PV1.52']['PV1.52.9'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningAuthority.id = pv1Seg['PV1.52']['PV1.52.9'].toString(); }
					else delete pid[index].PV1[indexPV1].otherHealthcareProvider.assigningAuthority;
				}
				if (pv1Seg['PV1.52']['PV1.52.10'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.nameTypeCode = pv1Seg['PV1.52']['PV1.52.10'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.11'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.identifierCheckDigit = pv1Seg['PV1.52']['PV1.52.11'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.12'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.codeIdentifyingCheckDigitScheme = pv1Seg['PV1.52']['PV1.52.12'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.13'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.identifierTypeCode = pv1Seg['PV1.52']['PV1.52.13'].toString(); }
				pid[index].PV1[indexPV1].otherHealthcareProvider.assigningFacility = {};
				if (pv1Seg['PV1.52']['PV1.52.14']['PV1.52.14.1'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningFacility.id = pv1Seg['PV1.52']['PV1.52.14']['PV1.52.14.1'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.14']['PV1.52.14.2'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningFacility.universalId = pv1Seg['PV1.52']['PV1.52.14']['PV1.52.14.2'].toString(); }
				if (pv1Seg['PV1.52']['PV1.52.14']['PV1.52.14.3'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningFacility.universalIdType = pv1Seg['PV1.52']['PV1.52.14']['PV1.52.14.3'].toString(); }
				if (Object.keys(pid[index].PV1[indexPV1].otherHealthcareProvider.assigningFacility).length == 0) {
					if (pv1Seg['PV1.52']['PV1.52.14'].toString() != "") { pid[index].PV1[indexPV1].otherHealthcareProvider.assigningFacility.id = pv1Seg['PV1.52']['PV1.52.14'].toString(); }
					else delete pid[index].PV1[indexPV1].otherHealthcareProvider.assigningFacility;
				}
				if (Object.keys(pid[index].PV1[indexPV1].otherHealthcareProvider).length == 0) {
					delete pid[index].PV1[indexPV1].otherHealthcareProvider;
				}

				// clean up PV1 and increment indexPV1
				if (Object.keys(pid[index].PV1[indexPV1]).length == 0) {
					delete pid[index].PV1[indexPV1];
				} else {
					indexPV1++;
				}
			}

			// Clean up the PV1 object if it's empty
			if (pid[index].PV1[0] == null){
				delete pid[index].PV1;
			}
		}

		// Increment the PID index
		index++;
	}
}

// Optional: save a copy of the completed PID segment for reference later
//channelMap.put("pid", pid); // uncomment to save

// If it has content, add the PID segment to the hl7JSON object
if (pid && Object.keys(pid).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.PID = pid;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

// Pull out the PID JSON to embed the PV1 segments in
var pid = JSON.parse($('hl7JSON')).content.PID;

// PID iteration, in order to grab related PV1's
if (msg['PID'].length() > 0) {

	// PID is required to have a PV1, but if the PID was blank and was removed from the JSON, just create a new one to add the PV1 into
	if (!pid) {
		pid = [{}];
	}

	var index = 0;
	for each (seg in msg.PID) {

		// PV1 (PID)
		var pv1Group = getSegmentsAfter(msg,seg,'PV1',false);
		if (pv1Group.length > 0 && pid) {
			var indexPV1 = 0;

			// PV1 is required for PV2, so create it if it's not already present in the PID object
			if (!pid[index].PV1) {
				pid[index].PV1 = [{}];
			}

			for each (pv1Seg in pv1Group) {

				// PV2 (PV1) [PID]
				var pv2Group = getSegmentsAfter(msg,pv1Seg,'PV2',true);
				if (pv2Group.length > 0 && pid[index].PV1) {
					pid[index].PV1[indexPV1].PV2 = [];
					var indexPV2 = 0;
					for each (pv2Seg in pv2Group) {
						pid[index].PV1[indexPV1].PV2[indexPV2] = {};

						// PV2.1, priorPendingLocation
						pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation = {};
						if (pv2Seg['PV2.1']['PV2.1.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.pointOfCare = pv2Seg['PV2.1']['PV2.1.1'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.room = pv2Seg['PV2.1']['PV2.1.2'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.3'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.bed = pv2Seg['PV2.1']['PV2.1.3'].toString(); }
						pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.facility = {};
						if (pv2Seg['PV2.1']['PV2.1.4']['PV2.1.4.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.facility.nameSpaceId =  pv2Seg['PV2.1']['PV2.1.4']['PV2.1.4.1'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.4']['PV2.1.4.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.facility.universalId =  pv2Seg['PV2.1']['PV2.1.4']['PV2.1.4.2'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.4']['PV2.1.4.3'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.facility.universalIdType =  pv2Seg['PV2.1']['PV2.1.4']['PV2.1.4.3'].toString(); }
						if (Object.keys(pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.facility).length == 0) {
							if (pv2Seg['PV2.1']['PV2.1.4'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.facility.nameSpaceId = pv2Seg['PV2.1']['PV2.1.4'].toString(); }
							else delete pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.facility;
						}
						if (pv2Seg['PV2.1']['PV2.1.5'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.locationStatus = pv2Seg['PV2.1']['PV2.1.5'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.6'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.personLocationType = pv2Seg['PV2.1']['PV2.1.6'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.7'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.building = pv2Seg['PV2.1']['PV2.1.7'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.8'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.floor = pv2Seg['PV2.1']['PV2.1.8'].toString(); }
						if (pv2Seg['PV2.1']['PV2.1.9'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation.locationDescription = pv2Seg['PV2.1']['PV2.1.9'].toString(); }
						if (Object.keys(pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation).length == 0) {
							delete pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation;
						}

						// PV2.2, accommodationCode
						pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode = {};
						if (pv2Seg['PV2.2']['PV2.2.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode.id = pv2Seg['PV2.2']['PV2.2.1'].toString(); }
						if (pv2Seg['PV2.2']['PV2.2.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode.text = pv2Seg['PV2.2']['PV2.2.2'].toString(); }
						if (pv2Seg['PV2.2']['PV2.2.3'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode.codingSystem = pv2Seg['PV2.2']['PV2.2.3'].toString(); }
						if (pv2Seg['PV2.2']['PV2.2.4'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode.altId = pv2Seg['PV2.2']['PV2.2.4'].toString(); }
						if (pv2Seg['PV2.2']['PV2.2.5'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode.altText = pv2Seg['PV2.2']['PV2.2.5'].toString(); }
						if (pv2Seg['PV2.2']['PV2.2.6'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode.altCodingSystem = pv2Seg['PV2.2']['PV2.2.6'].toString(); }
						if (Object.keys(pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode).length == 0) {
							delete pid[index].PV1[indexPV1].PV2[indexPV2].accommodationCode;
						}

						// PV2.3, admitReason
						pid[index].PV1[indexPV1].PV2[indexPV2].admitReason = {};
						if (pv2Seg['PV2.3']['PV2.3.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].admitReason.id = pv2Seg['PV2.3']['PV2.3.1'].toString(); }
						if (pv2Seg['PV2.3']['PV2.3.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].admitReason.text = pv2Seg['PV2.3']['PV2.3.2'].toString(); }
						if (pv2Seg['PV2.3']['PV2.3.3'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].admitReason.codingSystem = pv2Seg['PV2.3']['PV2.3.3'].toString(); }
						if (pv2Seg['PV2.3']['PV2.3.4'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].admitReason.altId = pv2Seg['PV2.3']['PV2.3.4'].toString(); }
						if (pv2Seg['PV2.3']['PV2.3.5'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].admitReason.altText = pv2Seg['PV2.3']['PV2.3.5'].toString(); }
						if (pv2Seg['PV2.3']['PV2.3.6'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].admitReason.altCodingSystem = pv2Seg['PV2.3']['PV2.3.6'].toString(); }
						if (Object.keys(pid[index].PV1[indexPV1].PV2[indexPV2].admitReason).length == 0) {
							delete pid[index].PV1[indexPV1].PV2[indexPV2].admitReason;
						}

						// PV2.4, transferReason
						pid[index].PV1[indexPV1].PV2[indexPV2].transferReason = {};
						if (pv2Seg['PV2.4']['PV2.4.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].transferReason.id = pv2Seg['PV2.4']['PV2.4.1'].toString(); }
						if (pv2Seg['PV2.4']['PV2.4.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].transferReason.text = pv2Seg['PV2.4']['PV2.4.2'].toString(); }
						if (pv2Seg['PV2.4']['PV2.4.3'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].transferReason.codingSystem = pv2Seg['PV2.4']['PV2.4.3'].toString(); }
						if (pv2Seg['PV2.4']['PV2.4.4'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].transferReason.altId = pv2Seg['PV2.4']['PV2.4.4'].toString(); }
						if (pv2Seg['PV2.4']['PV2.4.5'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].transferReason.altText = pv2Seg['PV2.4']['PV2.4.5'].toString(); }
						if (pv2Seg['PV2.4']['PV2.4.6'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].transferReason.altCodingSystem = pv2Seg['PV2.4']['PV2.4.6'].toString(); }
						if (Object.keys(pid[index].PV1[indexPV1].PV2[indexPV2].transferReason).length == 0) {
							delete pid[index].PV1[indexPV1].PV2[indexPV2].transferReason;
						}

						// PV2.5, patientValuables
						pid[index].PV1[indexPV1].PV2[indexPV2].patientValuables = [];
						for each (field in pv2Seg['PV2.5']) {
							if (field['PV2.5.1'].toString() != "") {
								pid[index].PV1[indexPV1].PV2[indexPV2].patientValuables.push(field['PV2.5.1'].toString());
							}
						}
						if (pid[index].PV1[indexPV1].PV2[indexPV2].patientValuables.length == 0) {
							delete pid[index].PV1[indexPV1].PV2[indexPV2].patientValuables;
						}

						// PV2.6, patientValuablesLocation
						if (pv2Seg['PV2.6']['PV2.6.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].patientValuablesLocation = pv2Seg['PV2.6']['PV2.6.1'].toString(); }

						// PV2.7, visitUserCode
						if (pv2Seg['PV2.7']['PV2.7.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].visitUserCode = pv2Seg['PV2.7']['PV2.7.1'].toString(); }

						// PV2.8, expectedAdmitDateTime
						if (pv2Seg['PV2.8']['PV2.8.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].expectedAdmitDateTime = pv2Seg['PV2.8']['PV2.8.1'].toString(); }

						// PV2.9, expectedDischargeDateTime
						if (pv2Seg['PV2.9']['PV2.9.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].expectedDischargeDateTime = pv2Seg['PV2.9']['PV2.9.1'].toString(); }

						// PV2.10, estimatedLengthInpatientStay
						if (pv2Seg['PV2.10']['PV2.10.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].estimatedLengthInpatientStay = pv2Seg['PV2.10']['PV2.10.1'].toString(); }

						// PV2.11, actualLengthInpatientStay
						if (pv2Seg['PV2.11']['PV2.11.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].actualLengthInpatientStay = pv2Seg['PV2.11']['PV2.11.1'].toString(); }

						// PV2.12, visitDescription
						if (pv2Seg['PV2.12']['PV2.12.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].visitDescription = pv2Seg['PV2.12']['PV2.12.1'].toString(); }

						// PV2.13, referralSourceCode
						pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode = {};
						if (pv2Seg['PV2.13']['PV2.13.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.id = pv2Seg['PV2.13']['PV2.13.1'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.lastName = pv2Seg['PV2.13']['PV2.13.2'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.3'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.firstName = pv2Seg['PV2.13']['PV2.13.3'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.4'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.middleInitOrName = pv2Seg['PV2.13']['PV2.13.4'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.5'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.suffix = pv2Seg['PV2.13']['PV2.13.5'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.6'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.prefix = pv2Seg['PV2.13']['PV2.13.6'].toString(); }
