						if (pv2Seg['PV2.13']['PV2.13.8'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.sourceTable = pv2Seg['PV2.13']['PV2.13.8'].toString(); }
						pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningAuthority = {};
						if (pv2Seg['PV2.13']['PV2.13.9']['PV2.13.9.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningAuthority.id = pv2Seg['PV2.13']['PV2.13.9']['PV2.13.9.1'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.9']['PV2.13.9.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningAuthority.universalId = pv2Seg['PV2.13']['PV2.13.9']['PV2.13.9.2'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.9']['PV2.13.9.3'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningAuthority.universalIdType = pv2Seg['PV2.13']['PV2.13.9']['PV2.13.9.3'].toString(); }
						if (Object.keys(pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningAuthority).length == 0) {
							if (pv2Seg['PV2.13']['PV2.13.9'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningAuthority.id = pv2Seg['PV2.13']['PV2.13.9'].toString(); }
							else delete pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningAuthority;
						}
						if (pv2Seg['PV2.13']['PV2.13.10'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.nameTypeCode = pv2Seg['PV2.13']['PV2.13.10'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.11'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.identifierCheckDigit = pv2Seg['PV2.13']['PV2.13.11'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.12'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.codeIdentifyingCheckDigitScheme = pv2Seg['PV2.13']['PV2.13.12'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.13'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.identifierTypeCode = pv2Seg['PV2.13']['PV2.13.13'].toString(); }
						pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningFacility = {};
						if (pv2Seg['PV2.13']['PV2.13.14']['PV2.13.14.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningFacility.id = pv2Seg['PV2.13']['PV2.13.14']['PV2.13.14.1'].toString(); }
						if (pv2Seg['PV2.13']['PV2.13.14']['PV2.13.14.2'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].referralSourceCode.assigningFacility.universalId = pv2Seg['PV2.13']['PV2.13.14']['PV2.13.14.2'].toString(); }
