						// PV2.14, previousServiceDate
						if (pv2Seg['PV2.14']['PV2.14.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].previousServiceDate = pv2Seg['PV2.14']['PV2.14.1'].toString(); }

						// PV2.15, employmentIllnessRelatedIndicator
						if (pv2Seg['PV2.15']['PV2.15.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].employmentIllnessRelatedIndicator = pv2Seg['PV2.15']['PV2.15.1'].toString(); }

						// PV2.16, purgeStatusCode
						if (pv2Seg['PV2.16']['PV2.16.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].purgeStatusCode = pv2Seg['PV2.16']['PV2.16.1'].toString(); }

						// PV2.17, purgeStatusDate
						if (pv2Seg['PV2.17']['PV2.17.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].purgeStatusDate = pv2Seg['PV2.17']['PV2.17.1'].toString(); }

						// PV2.18, specialProgramCode
						if (pv2Seg['PV2.18']['PV2.18.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].specialProgramCode = pv2Seg['PV2.18']['PV2.18.1'].toString(); }

						// PV2.19, retentionIndicator
						if (pv2Seg['PV2.19']['PV2.19.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].retentionIndicator = pv2Seg['PV2.19']['PV2.19.1'].toString(); }

						// PV2.20, expectedNumInsurancePlans
						if (pv2Seg['PV2.20']['PV2.20.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].expectedNumInsurancePlans = pv2Seg['PV2.20']['PV2.20.1'].toString(); }

						// PV2.21, visitPublicityCode
						if (pv2Seg['PV2.21']['PV2.21.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].visitPublicityCode = pv2Seg['PV2.21']['PV2.21.1'].toString(); }

						// PV2.22, visitProtectionIndicator
						if (pv2Seg['PV2.22']['PV2.22.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].visitProtectionIndicator = pv2Seg['PV2.22']['PV2.22.1'].toString(); }

						// PV2.23, clinicOrganizationName
						pid[index].PV1[indexPV1].PV2[indexPV2].clinicOrganizationName = [];
						for each (field in pv2Seg['PV2.23']) {
							var block = {};
							if (field['PV2.23.1'].toString() != "") { block.organizationName = field['PV2.23.1'].toString(); }
							if (field['PV2.23.2'].toString() != "") { block.organizationNameTypeCode = field['PV2.23.2'].toString(); }
							if (field['PV2.23.3'].toString() != "") { block.idNumber = field['PV2.23.3'].toString(); }
							if (field['PV2.23.4'].toString() != "") { block.identifierCheckDigit = field['PV2.23.4'].toString(); }
							if (field['PV2.23.5'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['PV2.23.5'].toString(); }
							block.assigningAuthority = {};
							if (field['PV2.23.6']['PV2.23.6.1'].toString() != "") { block.assigningAuthority.namespaceId = field['PV2.23.6']['PV2.23.6.1'].toString(); }
							if (field['PV2.23.6']['PV2.23.6.2'].toString() != "") { block.assigningAuthority.universalId = field['PV2.23.6']['PV2.23.6.2'].toString(); }
							if (field['PV2.23.6']['PV2.23.6.3'].toString() != "") { block.assigningAuthority.universalIdType = field['PV2.23.6']['PV2.23.6.3'].toString(); }
							if (Object.keys(block.assigningAuthority).length == 0) {
								if (field['PV2.23.6'].toString() != "") { block.assigningAuthority.namespaceId = field['PV2.23.6'].toString(); }
								else delete block.assigningAuthority;
							}
							if (field['PV2.23.7'].toString() != "") { block.idTypeCode = field['PV2.23.7'].toString(); }
							block.assigningFacility = {};
							if (field['PV2.23.8']['PV2.23.8.1'].toString() != "") { block.assigningFacility.namespaceId = field['PV2.23.8']['PV2.23.8.1'].toString(); }
							if (field['PV2.23.8']['PV2.23.8.2'].toString() != "") { block.assigningFacility.universalId = field['PV2.23.8']['PV2.23.8.2'].toString(); }
							if (field['PV2.23.8']['PV2.23.8.3'].toString() != "") { block.assigningFacility.universalIdType = field['PV2.23.8']['PV2.23.8.3'].toString(); }
							if (Object.keys(block.assigningFacility).length == 0) {
								if (field['PV2.23.8'].toString() != "") { block.assigningFacility.namespaceId = field['PV2.23.8'].toString(); }
								else delete block.assigningFacility;
							}
							if (Object.keys(block).length > 0) {
