						// PV2.31, recurringServiceCode
						if (pv2Seg['PV2.31']['PV2.31.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].recurringServiceCode = pv2Seg['PV2.31']['PV2.31.1'].toString(); }

