						// PV2.34, militaryPartnershipCode
						if (pv2Seg['PV2.34']['PV2.34.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].militaryPartnershipCode = pv2Seg['PV2.34']['PV2.34.1'].toString(); }

						// PV2.35, militaryNonAvailabilityCode
						if (pv2Seg['PV2.35']['PV2.35.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].militaryNonAvailabilityCode = pv2Seg['PV2.35']['PV2.35.1'].toString(); }

						// PV2.36, newbornBabyIndicator
						if (pv2Seg['PV2.36']['PV2.36.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].newbornBabyIndicator = pv2Seg['PV2.36']['PV2.36.1'].toString(); }

						// PV2.37, babyDetainedIndicator
