		// ORC.8, parentOrder
		orc[index].parentOrder = {};
		orc[index].parentOrder.parentsPlacerOrderNumber = {};
		if (seg['ORC.8']['ORC.8.1']['ORC.8.1.1'].toString() != "") { orc[index].parentOrder.parentsPlacerOrderNumber.id = seg['ORC.8']['ORC.8.1']['ORC.8.1.1'].toString(); }
		if (seg['ORC.8']['ORC.8.1']['ORC.8.1.2'].toString() != "") { orc[index].parentOrder.parentsPlacerOrderNumber.namespaceId = seg['ORC.8']['ORC.8.1']['ORC.8.1.2'].toString(); }
		if (seg['ORC.8']['ORC.8.1']['ORC.8.1.3'].toString() != "") { orc[index].parentOrder.parentsPlacerOrderNumber.universalId = seg['ORC.8']['ORC.8.1']['ORC.8.1.3'].toString(); }
