				if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { orc[index].nte[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1'].toString(); }

				// NTE.2, sourceOfComment
				if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { orc[index].nte[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1'].toString(); }

				// NTE.3, comment
				orc[index].nte[indexNTE].comment = [];
				for each (field in nteSeg['NTE.3']) {
					var block = {};
					if (field['NTE.3.1'].toString() != "") { 
						orc[index].nte[indexNTE].comment.push(field['NTE.3.1'].toString());
					}
				}

				// clean up NTE and increment index
				if (Object.keys(orc[index].nte[indexNTE]).length == 0) {
					delete orc[index].nte[indexNTE];
				} else {
					indexNTE++;
				}
			}
		}

		// clean up ORC and increment index
		if (Object.keys(orc[index]).length == 0) {
			delete orc[index];
		} else {
			index++;
		}
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// If it has content, add the AL1 segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

// Pull out the ORC JSON to embed the OBR segments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a OBR, but if the ORC was blank and was removed from the JSON, just create a new one to add the OBR into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// OBR (ORC for ORM)
		var obrGroup = getSegmentsAfter(msg,seg,'OBR',false);
		if (obrGroup.length > 0) {
			orc[index].OBR = [];
			var indexOBR = 0;
			for each (obrSeg in obrGroup) {
				orc[index].OBR[indexOBR] = {};

				// OBR.1, setId
				if (obrSeg['OBR.1']['OBR.1.1'].toString() != "") { orc[index].OBR[indexOBR].setId = obrSeg['OBR.1']['OBR.1.1'].toString(); }

				// OBR.2, placerOrderNumber
				orc[index].OBR[indexOBR].placerOrderNumber = {};
				if (obrSeg['OBR.2']['OBR.2.1'].toString() != "") { orc[index].OBR[indexOBR].placerOrderNumber.id = obrSeg['OBR.2']['OBR.2.1'].toString(); }
				if (obrSeg['OBR.2']['OBR.2.2'].toString() != "") { orc[index].OBR[indexOBR].placerOrderNumber.namespaceId = obrSeg['OBR.2']['OBR.2.2'].toString(); }
				if (obrSeg['OBR.2']['OBR.2.3'].toString() != "") { orc[index].OBR[indexOBR].placerOrderNumber.universalId = obrSeg['OBR.2']['OBR.2.3'].toString(); }
				if (obrSeg['OBR.2']['OBR.2.4'].toString() != "") { orc[index].OBR[indexOBR].placerOrderNumber.universalIdType = obrSeg['OBR.2']['OBR.2.4'].toString(); }
				if (Object.keys(orc[index].OBR[indexOBR].placerOrderNumber).length == 0) {
					delete orc[index].placerOrderNumber;
				}

				// OBR.3, fillerOrderNumber
				orc[index].OBR[indexOBR].fillerOrderNumber = {};
				if (obrSeg['OBR.3']['OBR.3.1'].toString() != "") { orc[index].OBR[indexOBR].fillerOrderNumber.id = obrSeg['OBR.3']['OBR.3.1'].toString(); }
				if (obrSeg['OBR.3']['OBR.3.2'].toString() != "") { orc[index].OBR[indexOBR].fillerOrderNumber.namespaceId = obrSeg['OBR.3']['OBR.3.2'].toString(); }
				if (obrSeg['OBR.3']['OBR.3.3'].toString() != "") { orc[index].OBR[indexOBR].fillerOrderNumber.universalId = obrSeg['OBR.3']['OBR.3.3'].toString(); }
				if (obrSeg['OBR.3']['OBR.3.4'].toString() != "") { orc[index].OBR[indexOBR].fillerOrderNumber.universalIdType = obrSeg['OBR.3']['OBR.3.4'].toString(); }
				if (Object.keys(orc[index].OBR[indexOBR].fillerOrderNumber).length == 0) {
					delete orc[index].fillerOrderNumber;
				}

				// OBR.4, universalServiceID
				orc[index].OBR[indexOBR].universalServiceID = {};
				if (obrSeg['OBR.4']['OBR.4.1'].toString() != "") { orc[index].OBR[indexOBR].universalServiceID.id = obrSeg['OBR.4']['OBR.4.1'].toString(); }
				if (obrSeg['OBR.4']['OBR.4.2'].toString() != "") { orc[index].OBR[indexOBR].universalServiceID.text = obrSeg['OBR.4']['OBR.4.2'].toString(); }
				if (obrSeg['OBR.4']['OBR.4.3'].toString() != "") { orc[index].OBR[indexOBR].universalServiceID.codingSystem = obrSeg['OBR.4']['OBR.4.3'].toString(); }
				if (obrSeg['OBR.4']['OBR.4.4'].toString() != "") { orc[index].OBR[indexOBR].universalServiceID.altId = obrSeg['OBR.4']['OBR.4.4'].toString(); }
				if (obrSeg['OBR.4']['OBR.4.5'].toString() != "") { orc[index].OBR[indexOBR].universalServiceID.altText = obrSeg['OBR.4']['OBR.4.5'].toString(); }
				if (obrSeg['OBR.4']['OBR.4.6'].toString() != "") { orc[index].OBR[indexOBR].universalServiceID.altCodingSystem = obrSeg['OBR.4']['OBR.4.6'].toString(); }
				if (Object.keys(orc[index].OBR[indexOBR].universalServiceID).length == 0) {
