						else delete block.assigningAuthority;
					}
					if (field['OBR.16.10'].toString() != "") { block.nameTypeCode = field['OBR.16.10'].toString(); }
					if (field['OBR.16.11'].toString() != "") { block.identifierCheckDigit = field['OBR.16.11'].toString(); }
					if (field['OBR.16.12'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['OBR.16.12'].toString(); }
