					if (field['OBR.33.7'].toString() != "") { block.degree = field['OBR.33.7'].toString(); }
					if (field['OBR.33.8'].toString() != "") { block.sourceTable = field['OBR.33.8'].toString(); }
					block.assigningAuthority = {};
					if (field['OBR.33.9']['OBR.33.9.1'].toString() != "") { block.assigningAuthority.id = field['OBR.33.9']['OBR.33.9.1'].toString(); }
					if (field['OBR.33.9']['OBR.33.9.2'].toString() != "") { block.assigningAuthority.universalId = field['OBR.33.9']['OBR.33.9.2'].toString(); }
					if (field['OBR.33.9']['OBR.33.9.3'].toString() != "") { block.assigningAuthority.universalIdType = field['OBR.33.9']['OBR.33.9.3'].toString(); }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['OBR.33.9'].toString() != "") { block.assigningAuthority.id = field['OBR.33.9'].toString(); }
						else delete block.assigningAuthority;
					}
					if (field['OBR.33.10'].toString() != "") { block.nameTypeCode = field['OBR.33.10'].toString(); }
					if (field['OBR.33.11'].toString() != "") { block.identifierCheckDigit = field['OBR.33.11'].toString(); }
					if (field['OBR.33.12'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['OBR.33.12'].toString(); }
					if (field['OBR.33.13'].toString() != "") { block.identifierTypeCode = field['OBR.33.13'].toString(); }
					block.assigningFacility = {};
					if (field['OBR.33.14']['OBR.33.14.1'].toString() != "") { block.assigningFacility.id = field['OBR.33.14']['OBR.33.14.1'].toString(); }
					if (field['OBR.33.14']['OBR.33.14.2'].toString() != "") { block.assigningFacility.universalId = field['OBR.33.14']['OBR.33.14.2'].toString(); }
					if (field['OBR.33.14']['OBR.33.14.3'].toString() != "") { block.assigningFacility.universalIdType = field['OBR.33.14']['OBR.33.14.3'].toString(); }
					if (Object.keys(block.assigningFacility).length == 0) {
						if (field['OBR.33.14'].toString() != "") { block.assigningFacility.id = field['OBR.33.14'].toString(); }
						else delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						orc[index].OBR[indexOBR].assistantResultInterpreter.push(block);
					}
				}
				if (orc[index].OBR[indexOBR].assistantResultInterpreter.length == 0) {
					delete orc[index].OBR[indexOBR].assistantResultInterpreter;
				}

				// OBR.34, technician
				orc[index].OBR[indexOBR].technician = [];
				for each (field in obrSeg['OBR.34']) {
					var block = {};
					if (field['OBR.34.1'].toString() != "") { block.id = field['OBR.34.1'].toString(); }
					if (field['OBR.34.2'].toString() != "") { block.lastName = field['OBR.34.2'].toString(); }
					if (field['OBR.34.3'].toString() != "") { block.firstName = field['OBR.34.3'].toString(); }
					if (field['OBR.34.4'].toString() != "") { block.middleInitOrName = field['OBR.34.4'].toString(); }
					if (field['OBR.34.5'].toString() != "") { block.suffix = field['OBR.34.5'].toString(); }
					if (field['OBR.34.6'].toString() != "") { block.prefix = field['OBR.34.6'].toString(); }
					if (field['OBR.34.7'].toString() != "") { block.degree = field['OBR.34.7'].toString(); }
					if (field['OBR.34.8'].toString() != "") { block.sourceTable = field['OBR.34.8'].toString(); }
					block.assigningAuthority = {};
					if (field['OBR.34.9']['OBR.34.9.1'].toString() != "") { block.assigningAuthority.id = field['OBR.34.9']['OBR.34.9.1'].toString(); }
					if (field['OBR.34.9']['OBR.34.9.2'].toString() != "") { block.assigningAuthority.universalId = field['OBR.34.9']['OBR.34.9.2'].toString(); }
					if (field['OBR.34.9']['OBR.34.9.3'].toString() != "") { block.assigningAuthority.universalIdType = field['OBR.34.9']['OBR.34.9.3'].toString(); }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['OBR.34.9'].toString() != "") { block.assigningAuthority.id = field['OBR.34.9'].toString(); }
						else delete block.assigningAuthority;
					}
					if (field['OBR.34.10'].toString() != "") { block.nameTypeCode = field['OBR.34.10'].toString(); }
					if (field['OBR.34.11'].toString() != "") { block.identifierCheckDigit = field['OBR.34.11'].toString(); }
					if (field['OBR.34.12'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['OBR.34.12'].toString(); }
					if (field['OBR.34.13'].toString() != "") { block.identifierTypeCode = field['OBR.34.13'].toString(); }
					block.assigningFacility = {};
					if (field['OBR.34.14']['OBR.34.14.1'].toString() != "") { block.assigningFacility.id = field['OBR.34.14']['OBR.34.14.1'].toString(); }
					if (field['OBR.34.14']['OBR.34.14.2'].toString() != "") { block.assigningFacility.universalId = field['OBR.34.14']['OBR.34.14.2'].toString(); }
					if (field['OBR.34.14']['OBR.34.14.3'].toString() != "") { block.assigningFacility.universalIdType = field['OBR.34.14']['OBR.34.14.3'].toString(); }
					if (Object.keys(block.assigningFacility).length == 0) {
						delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						orc[index].OBR[indexOBR].technician.push(block);
					}
				}
				if (orc[index].OBR[indexOBR].technician.length == 0) {
					delete orc[index].OBR[indexOBR].technician;
				}

				// OBR.35, transcriptionist
				orc[index].OBR[indexOBR].transcriptionist = [];
				for each (field in obrSeg['OBR.35']) {
					var block = {};
					if (field['OBR.35.1'].toString() != "") { block.id = field['OBR.35.1'].toString(); }
					if (field['OBR.35.2'].toString() != "") { block.lastName = field['OBR.35.2'].toString(); }
					if (field['OBR.35.3'].toString() != "") { block.firstName = field['OBR.35.3'].toString(); }
					if (field['OBR.35.4'].toString() != "") { block.middleInitOrName = field['OBR.35.4'].toString(); }
					if (field['OBR.35.5'].toString() != "") { block.suffix = field['OBR.35.5'].toString(); }
					if (field['OBR.35.6'].toString() != "") { block.prefix = field['OBR.35.6'].toString(); }
					if (field['OBR.35.7'].toString() != "") { block.degree = field['OBR.35.7'].toString(); }
					if (field['OBR.35.8'].toString() != "") { block.sourceTable = field['OBR.35.8'].toString(); }
					block.assigningAuthority = {};
					if (field['OBR.35.9']['OBR.35.9.1'].toString() != "") { block.assigningAuthority.id = field['OBR.35.9']['OBR.35.9.1'].toString(); }
					if (field['OBR.35.9']['OBR.35.9.2'].toString() != "") { block.assigningAuthority.universalId = field['OBR.35.9']['OBR.35.9.2'].toString(); }
					if (field['OBR.35.9']['OBR.35.9.3'].toString() != "") { block.assigningAuthority.universalIdType = field['OBR.35.9']['OBR.35.9.3'].toString(); }
					if (Object.keys(block.assigningAuthority).length == 0) {
						if (field['OBR.35.9'].toString() != "") { block.assigningAuthority.id = field['OBR.35.9'].toString(); }
						else delete block.assigningAuthority;
					}
					if (field['OBR.35.10'].toString() != "") { block.nameTypeCode = field['OBR.35.10'].toString(); }
					if (field['OBR.35.11'].toString() != "") { block.identifierCheckDigit = field['OBR.35.11'].toString(); }
					if (field['OBR.35.12'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['OBR.35.12'].toString(); }
					if (field['OBR.35.13'].toString() != "") { block.identifierTypeCode = field['OBR.35.13'].toString(); }
					block.assigningFacility = {};
					if (field['OBR.35.14']['OBR.35.14.1'].toString() != "") { block.assigningFacility.id = field['OBR.35.14']['OBR.35.14.1'].toString(); }
					if (field['OBR.35.14']['OBR.35.14.2'].toString() != "") { block.assigningFacility.universalId = field['OBR.35.14']['OBR.35.14.2'].toString(); }
					if (field['OBR.35.14']['OBR.35.14.3'].toString() != "") { block.assigningFacility.universalIdType = field['OBR.35.14']['OBR.35.14.3'].toString(); }
					if (Object.keys(block.assigningFacility).length == 0) {
						delete block.assigningFacility;
					}
					if (Object.keys(block).length > 0) {
						orc[index].OBR[indexOBR].transcriptionist.push(block);
					}
				}
				if (orc[index].OBR[indexOBR].transcriptionist.length == 0) {
					delete orc[index].OBR[indexOBR].transcriptionist;
				}

				// OBR.36, scheduledDateTime
				if (obrSeg['OBR.36']['OBR.36.1'].toString() != "") { orc[index].OBR[indexOBR].scheduledDateTime = obrSeg['OBR.36']['OBR.36.1'].toString(); }

				// OBR.37, numberOfSampleContainers
				if (obrSeg['OBR.37']['OBR.37.1'].toString() != "") { orc[index].OBR[indexOBR].numberOfSampleContainers = obrSeg['OBR.37']['OBR.37.1'].toString(); }

				// OBR.38, transportLogisticsOfCollectedSample
				orc[index].OBR[indexOBR].transportLogisticsOfCollectedSample = [];
				for each (field in obrSeg['OBR.38']) {
					var block = {};
					if (field['OBR.38.1'].toString() != "") { block.id = field['OBR.38.1'].toString(); }
					if (field['OBR.38.2'].toString() != "") { block.text = field['OBR.38.2'].toString(); }
					if (field['OBR.38.3'].toString() != "") { block.codingSystem = field['OBR.38.3'].toString(); }
					if (field['OBR.38.4'].toString() != "") { block.altId = field['OBR.38.4'].toString(); }
					if (field['OBR.38.5'].toString() != "") { block.altText = field['OBR.38.5'].toString(); }
					if (field['OBR.38.6'].toString() != "") { block.altCodingSystem = field['OBR.38.6'].toString(); }
					if (Object.keys(block).length > 0) {
						orc[index].OBR[indexOBR].transportLogisticsOfCollectedSample.push(block);
					}
				}
				if (orc[index].OBR[indexOBR].transportLogisticsOfCollectedSample.length == 0) {
					delete orc[index].OBR[indexOBR].transportLogisticsOfCollectedSample;
				}

				// OBR.39, collectorsComment
				orc[index].OBR[indexOBR].collectorsComment = [];
				for each (field in obrSeg['OBR.39']) {
					var block = {};
					if (field['OBR.39.1'].toString() != "") { block.id = field['OBR.39.1'].toString(); }
					if (field['OBR.39.2'].toString() != "") { block.text = field['OBR.39.2'].toString(); }
					if (field['OBR.39.3'].toString() != "") { block.codingSystem = field['OBR.39.3'].toString(); }
					if (field['OBR.39.4'].toString() != "") { block.altId = field['OBR.39.4'].toString(); }
					if (field['OBR.39.5'].toString() != "") { block.altText = field['OBR.39.5'].toString(); }
					if (field['OBR.39.6'].toString() != "") { block.altCodingSystem = field['OBR.39.6'].toString(); }
					if (Object.keys(block).length > 0) {
						orc[index].OBR[indexOBR].collectorsComment.push(block);
					}
				}
				if (orc[index].OBR[indexOBR].collectorsComment.length == 0) {
					delete orc[index].OBR[indexOBR].collectorsComment;
				}

				// OBR.40, transportArrangementResponsibility
				orc[index].OBR[indexOBR].transportArrangementResponsibility = {};
				if (obrSeg['OBR.40']['OBR.40.1'].toString() != "") { orc[index].OBR[indexOBR].transportArrangementResponsibility.id = obrSeg['OBR.40']['OBR.40.1'].toString(); }
				if (obrSeg['OBR.40']['OBR.40.2'].toString() != "") { orc[index].OBR[indexOBR].transportArrangementResponsibility.text = obrSeg['OBR.40']['OBR.40.2'].toString(); }
				if (obrSeg['OBR.40']['OBR.40.3'].toString() != "") { orc[index].OBR[indexOBR].transportArrangementResponsibility.codingSystem = obrSeg['OBR.40']['OBR.40.3'].toString(); }
				if (obrSeg['OBR.40']['OBR.40.4'].toString() != "") { orc[index].OBR[indexOBR].transportArrangementResponsibility.altId = obrSeg['OBR.40']['OBR.40.4'].toString(); }
				if (obrSeg['OBR.40']['OBR.40.5'].toString() != "") { orc[index].OBR[indexOBR].transportArrangementResponsibility.altText = obrSeg['OBR.40']['OBR.40.5'].toString(); }
				if (obrSeg['OBR.40']['OBR.40.6'].toString() != "") { orc[index].OBR[indexOBR].transportArrangementResponsibility.altCodingSystem = obrSeg['OBR.40']['OBR.40.6'].toString(); }
				if (Object.keys(orc[index].OBR[indexOBR].transportArrangementResponsibility).length == 0) {
					delete orc[index].OBR[indexOBR].transportArrangementResponsibility;
				}

				// OBR.41, transportArranged
				if (obrSeg['OBR.41']['OBR.41.1'].toString() != "") { orc[index].OBR[indexOBR].transportArranged = obrSeg['OBR.41']['OBR.41.1'].toString(); }

				// OBR.42, escortRequired
				if (obrSeg['OBR.42']['OBR.42.1'].toString() != "") { orc[index].OBR[indexOBR].escortRequired = obrSeg['OBR.42']['OBR.42.1'].toString(); }

				// OBR.43, plannedPatientTransportComment
				orc[index].OBR[indexOBR].plannedPatientTransportComment = [];
				for each (field in obrSeg['OBR.43']) {
					var block = {};
					if (field['OBR.43.1'].toString() != "") { block.id = field['OBR.43.1'].toString(); }
					if (field['OBR.43.2'].toString() != "") { block.text = field['OBR.43.2'].toString(); }
					if (field['OBR.43.3'].toString() != "") { block.codingSystem = field['OBR.43.3'].toString(); }
					if (field['OBR.43.4'].toString() != "") { block.altId = field['OBR.43.4'].toString(); }
					if (field['OBR.43.5'].toString() != "") { block.altText = field['OBR.43.5'].toString(); }
					if (field['OBR.43.6'].toString() != "") { block.altCodingSystem = field['OBR.43.6'].toString(); }
					if (Object.keys(block).length > 0) {
						orc[index].OBR[indexOBR].plannedPatientTransportComment.push(block);
					}
				}
				if (orc[index].OBR[indexOBR].plannedPatientTransportComment.length == 0) {
					delete orc[index].OBR[indexOBR].plannedPatientTransportComment;
				}

				// clean up OBR and increment index
				if (Object.keys(orc[index].OBR[indexOBR]).length == 0) {
					delete orc[index].OBR[indexOBR];
				} else {
					indexOBR++;
				}
			}

			// Clean up the OBR object if it's empty
			if (orc[index].OBR[0] == null){
				delete orc[index].OBR;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// If it has content, add the AL1 segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

// Pull out the ORC JSON to embed the OBR segments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC
if (msg['ORC'].length() > 0) {

	// ORC is a required to have an OBR, but if the ORC was blank and was removed from the JSON, just create a new one to add the OBR into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// OBR (ORC for ORM)
		var obrGroup = getSegmentsAfter(msg,seg,'OBR',false);
		if (obrGroup.length > 0) {
			var indexOBR = 0;

			// OBR is required for NTE and OBX, so create it if it's not already present in the ORC object
			if (!orc[index].OBR) {
				orc[index].OBR = [{}];
			}

			for each (obrSeg in obrGroup) {

				// NTE (OBR)
				var nteGroup = getSegmentsAfter(msg,obrSeg,'NTE',true);
				if (nteGroup.length > 0) {
					orc[index].OBR[indexOBR].NTE = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						orc[index].OBR[indexOBR].NTE[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { orc[index].OBR[indexOBR].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1'].toString(); }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { orc[index].OBR[indexOBR].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1'].toString(); }

						// NTE.3, comment
						orc[index].OBR[indexOBR].NTE[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								orc[index].OBR[indexOBR].NTE[indexNTE].comment.push(field['NTE.3.1'].toString());
							}
						}
						if (orc[index].OBR[indexOBR].NTE[indexNTE].comment.length == 0) {
							delete orc[index].OBR[indexOBR].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (orc[index].OBR[indexOBR].NTE.length == 0) {
							delete orc[index].OBR[indexOBR].NTE;
						} else {
							indexNTE++;
						}
					}
				}

				// OBX (OBR)
				var obxGroup = getSegmentsAfter(msg,obrSeg,'OBX',false);
				if (obxGroup.length > 0) {
					orc[index].OBR[indexOBR].OBX = [];
					var indexOBX = 0;
					for each (obxSeg in obxGroup) {
						orc[index].OBR[indexOBR].OBX[indexOBX] = {};

						// OBX.1, setId
						if (obxSeg['OBX.1']['OBX.1.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].setId = obxSeg['OBX.1']['OBX.1.1'].toString(); }

						// OBX.2, valueType
						if (obxSeg['OBX.2']['OBX.2.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].valueType = obxSeg['OBX.2']['OBX.2.1'].toString(); }

						// OBX.3, identifier
						orc[index].OBR[indexOBR].OBX[indexOBX].identifier = {};
						if (obxSeg['OBX.3']['OBX.3.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].identifier.id = obxSeg['OBX.3']['OBX.3.1'].toString(); }
						if (obxSeg['OBX.3']['OBX.3.2'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].identifier.text = obxSeg['OBX.3']['OBX.3.2'].toString(); }
						if (obxSeg['OBX.3']['OBX.3.3'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].identifier.codingSystem = obxSeg['OBX.3']['OBX.3.3'].toString(); }
						if (obxSeg['OBX.3']['OBX.3.4'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].identifier.altId = obxSeg['OBX.3']['OBX.3.4'].toString(); }
						if (obxSeg['OBX.3']['OBX.3.5'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].identifier.altText = obxSeg['OBX.3']['OBX.3.5'].toString(); }
						if (obxSeg['OBX.3']['OBX.3.6'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].identifier.altCodingSystem = obxSeg['OBX.3']['OBX.3.6'].toString(); }
						if (Object.keys(orc[index].OBR[indexOBR].OBX[indexOBX].identifier).length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].identifier;
						}

						// OBX.4, subId
						if (obxSeg['OBX.4']['OBX.4.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].subId = obxSeg['OBX.4']['OBX.4.1'].toString(); }

						// OBX.5, observationValue
						orc[index].OBR[indexOBR].OBX[indexOBX].observationValue = [];
						for each (field in obxSeg['OBX.5']) {
							if (field['OBX.5.1'].toString() != "" && field['OBX.5.1'].toString() != "pdf") {
							    orc[index].OBR[indexOBR].OBX[indexOBX].observationValue.push(field['OBX.5.1'].toString());
							} else if (field['OBX.5.1'].toString() != "" && field['OBX.5.1'].toString() === "pdf") {
							    orc[index].OBR[indexOBR].OBX[indexOBX].observationValue.push(field['OBX.5.3'].toString());
							} else if (field['OBX.5.5'].toString() != "") {
							    orc[index].OBR[indexOBR].OBX[indexOBX].observationValue.push(field['OBX.5.5'].toString());
							}
						}
						if (orc[index].OBR[indexOBR].OBX[indexOBX].observationValue.length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].observationValue;
						}

						// OBX.6, units
						orc[index].OBR[indexOBR].OBX[indexOBX].units = {};
						if (obxSeg['OBX.6']['OBX.6.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].units.id = obxSeg['OBX.6']['OBX.6.1'].toString(); }
						if (obxSeg['OBX.6']['OBX.6.2'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].units.text = obxSeg['OBX.6']['OBX.6.2'].toString(); }
						if (obxSeg['OBX.6']['OBX.6.3'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].units.codingSystem = obxSeg['OBX.6']['OBX.6.3'].toString(); }
						if (obxSeg['OBX.6']['OBX.6.4'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].units.altId = obxSeg['OBX.6']['OBX.6.4'].toString(); }
						if (obxSeg['OBX.6']['OBX.6.5'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].units.altText = obxSeg['OBX.6']['OBX.6.5'].toString(); }
						if (obxSeg['OBX.6']['OBX.6.6'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].units.altCodingSystem = obxSeg['OBX.6']['OBX.6.6'].toString(); }
						if (Object.keys(orc[index].OBR[indexOBR].OBX[indexOBX].units).length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].units;
						}

						// OBX.7, referenceRange
						if (obxSeg['OBX.7']['OBX.7.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].referenceRange = obxSeg['OBX.7']['OBX.7.1'].toString(); }

						// OBX.8, abnormalFlag
						orc[index].OBR[indexOBR].OBX[indexOBX].abnormalFlag = [];
						for each (field in obxSeg['OBX.8']) {
							if (field['OBX.8.1'].toString() != "") { 
								orc[index].OBR[indexOBR].OBX[indexOBX].abnormalFlag.push(field['OBX.8.1'].toString()); 
							}
						}
						if (orc[index].OBR[indexOBR].OBX[indexOBX].abnormalFlag.length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].abnormalFlag;
						}

						// OBX.9, probability
						if (obxSeg['OBX.9']['OBX.9.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].probability = obxSeg['OBX.9']['OBX.9.1'].toString(); }

						// OBX.10, natureOfAbnormal
						orc[index].OBR[indexOBR].OBX[indexOBX].natureOfAbnormal = [];
						for each (field in obxSeg['OBX.10']) {
							if (field['OBX.10.1'].toString() != "") { 
								orc[index].OBR[indexOBR].OBX[indexOBX].natureOfAbnormal.push(field['OBX.10.1'].toString()); 
							}
						}
						if (orc[index].OBR[indexOBR].OBX[indexOBX].natureOfAbnormal.length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].natureOfAbnormal;
						}

						// OBX.11, observeResultStatus
						if (obxSeg['OBX.11']['OBX.11.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].observeResultStatus = obxSeg['OBX.11']['OBX.11.1'].toString(); }

						// OBX.12, effectiveDateLastNormalValue
						if (obxSeg['OBX.12']['OBX.12.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].effectiveDateLastNormalValue = obxSeg['OBX.12']['OBX.12.1'].toString(); }

						// OBX.13, definedAccessChecks
						if (obxSeg['OBX.13']['OBX.13.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].definedAccessChecks = obxSeg['OBX.13']['OBX.13.1'].toString(); }

						// OBX.14, observationDateTime
						if (obxSeg['OBX.14']['OBX.14.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].observationDateTime = obxSeg['OBX.14']['OBX.14.1'].toString(); }

						// OBX.15, producersId
						orc[index].OBR[indexOBR].OBX[indexOBX].producersId = {};
						if (obxSeg['OBX.15']['OBX.15.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].producersId.id = obxSeg['OBX.15']['OBX.15.1'].toString(); }
						if (obxSeg['OBX.15']['OBX.15.2'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].producersId.text = obxSeg['OBX.15']['OBX.15.2'].toString(); }
						if (obxSeg['OBX.15']['OBX.15.3'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].producersId.codingSystem = obxSeg['OBX.15']['OBX.15.3'].toString(); }
						if (obxSeg['OBX.15']['OBX.15.4'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].producersId.altId = obxSeg['OBX.15']['OBX.15.4'].toString(); }
						if (obxSeg['OBX.15']['OBX.15.5'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].producersId.altText = obxSeg['OBX.15']['OBX.15.5'].toString(); }
						if (obxSeg['OBX.15']['OBX.15.6'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].producersId.altCodingSystem = obxSeg['OBX.15']['OBX.15.6'].toString(); }
						if (Object.keys(orc[index].OBR[indexOBR].OBX[indexOBX].producersId).length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].producersId;
						}

						// OBX.16, responsibleObserver
						orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver = {};
						if (obxSeg['OBX.16']['OBX.16.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.id = obxSeg['OBX.16']['OBX.16.1'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.2'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.lastName = obxSeg['OBX.16']['OBX.16.2'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.3'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.firstName = obxSeg['OBX.16']['OBX.16.3'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.4'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.middleInitOrName = obxSeg['OBX.16']['OBX.16.4'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.5'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.suffix = obxSeg['OBX.16']['OBX.16.5'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.6'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.prefix = obxSeg['OBX.16']['OBX.16.6'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.7'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.degree = obxSeg['OBX.16']['OBX.16.7'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.8'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.sourceTable = obxSeg['OBX.16']['OBX.16.8'].toString(); }
						orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningAuthority = {};
						if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningAuthority.id = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningAuthority.universalId = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningAuthority.universalIdType = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3'].toString(); }
						if (Object.keys(orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningAuthority).length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningAuthority;
						}
						if (obxSeg['OBX.16']['OBX.16.10'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.nameTypeCode = obxSeg['OBX.16']['OBX.16.10'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.11'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.identifierCheckDigit = obxSeg['OBX.16']['OBX.16.11'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.12'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.codeIdentifyingCheckDigitScheme = obxSeg['OBX.16']['OBX.16.12'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.13'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.identifierTypeCode = obxSeg['OBX.16']['OBX.16.13'].toString(); }
						orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningFacility = {};
						if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningFacility.id = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningFacility.universalId = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2'].toString(); }
						if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningFacility.universalIdType = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3'].toString(); }
						if (Object.keys(orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningFacility).length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver.assigningFacility;
						}
						if (Object.keys(orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver).length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].responsibleObserver;
						}

						// OBX.17, observationMethod
						orc[index].OBR[indexOBR].OBX[indexOBX].observationMethod = [];
						for each (field in obxSeg['OBX.17']) {
							var block = {};
							if (field['OBX.17.1'].toString() != "") { block.id = field['OBX.17.1'].toString(); }
							if (field['OBX.17.2'].toString() != "") { block.text = field['OBX.17.2'].toString(); }
							if (field['OBX.17.3'].toString() != "") { block.codingSystem = field['OBX.17.3'].toString(); }
							if (field['OBX.17.4'].toString() != "") { block.altId = field['OBX.17.4'].toString(); }
							if (field['OBX.17.5'].toString() != "") { block.altText = field['OBX.17.5'].toString(); }
							if (field['OBX.17.6'].toString() != "") { block.altCodingSystem = field['OBX.17.6'].toString(); }
							if (Object.keys(block).length > 0) {
								orc[index].OBR[indexOBR].OBX[indexOBX].observationMethod.push(block);
							}
						}
						if (orc[index].OBR[indexOBR].OBX[indexOBX].observationMethod.length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX].observationMethod;
						}

						// NTE (OBX)
						var nteGroup = getSegmentsAfter(msg,obxSeg,'NTE',true);
						if (nteGroup.length > 0) {
							orc[index].OBR[indexOBR].OBX[indexOBX].NTE = [];
							var indexNTE = 0;
							for each (nteSeg in nteGroup) {
								orc[index].OBR[indexOBR].OBX[indexOBX].NTE[indexNTE] = {};

								// NTE.1, setId
								if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1'].toString(); }

								// NTE.2, sourceOfComment
								if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { orc[index].OBR[indexOBR].OBX[indexOBX].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1'].toString(); }

								// NTE.3, comment
								orc[index].OBR[indexOBR].OBX[indexOBX].NTE[indexNTE].comment = [];
								for each (field in nteSeg['NTE.3']) {
									var block = {};
									if (field['NTE.3.1'].toString() != "") { 
										orc[index].OBR[indexOBR].OBX[indexOBX].NTE[indexNTE].comment.push(field['NTE.3.1'].toString());
									}
								}
								if (orc[index].OBR[indexOBR].OBX[indexOBX].NTE[indexNTE].comment.length == 0) {
									delete orc[index].OBR[indexOBR].OBX[indexOBX].NTE[indexNTE].comment;
								}

								// clean up NTE and increment index
								if (orc[index].OBR[indexOBR].OBX[indexOBX].NTE.length == 0) {
									delete orc[index].OBR[indexOBR].OBX[indexOBX].NTE;
								} else {
									indexNTE++;
								}
							}
						}

						// clean up OBX and increment index
						if (Object.keys(orc[index].OBR[indexOBR].OBX[indexOBX]).length == 0) {
							delete orc[index].OBR[indexOBR].OBX[indexOBX];
						} else {
							indexOBX++;
						}
					}
				}

				// clean up OBR and increment index
				if (Object.keys(orc[index].OBR[indexOBR]).length == 0) {
					delete orc[index].OBR[indexOBR];
				} else {
					indexOBR++;
				}
			}

			// Clean up the OBR object if it's empty
			if (orc[index].OBR[0] == null){
				delete orc[index].OBR;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// If it has content, add the AL1 segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

