		if (seg['CTI.3']['CTI.3.4'].toString() != "") { cti[index].studyScheduledTimePoint.altId = seg['CTI.3']['CTI.3.4'].toString(); }
		if (seg['CTI.3']['CTI.3.5'].toString() != "") { cti[index].studyScheduledTimePoint.altText = seg['CTI.3']['CTI.3.5'].toString(); }
		if (seg['CTI.3']['CTI.3.6'].toString() != "") { cti[index].studyScheduledTimePoint.altCodingSystem = seg['CTI.3']['CTI.3.6'].toString(); }
		if (Object.keys(cti[index].studyScheduledTimePoint).length == 0) {
			delete cti[index].studyScheduledTimePoint;
		}

		// clean up CTI and increment index
		if (Object.keys(cti[index]).length == 0) {
			delete cti[index];
		} else {
			index++;
		}
	}
}

